INSERT INTO events (name, description, time_informal, year, relevance) VALUES ('Invention of the Wheel', 'The wheel is invented, revolutionizing transportation and making it easier to move heavy objects.', 'Around 3500 BCE', -3500, 95);
INSERT INTO events (name, description, time_informal, year, relevance) VALUES ('Construction of the Great Pyramid of Giza', 'The Great Pyramid of Giza is built as a tomb for the Pharaoh Khufu.', 'Around 2560 BCE', -2560, 90);
INSERT INTO events (name, description, time_informal, year, relevance) VALUES ('Buddhas Enlightenment', 'Siddhartha Gautama attains enlightenment under the Bodhi tree, becoming the Buddha.', 'Around 500 BCE', -500, 85);
INSERT INTO events (name, description, time_informal, year, relevance) VALUES ('The Roman Republic is Established', 'The Roman Republic is founded, marking the beginning of the Roman civilization.', 'Around 509 BCE', -509, 90);
INSERT INTO events (name, description, time_informal, year, relevance) VALUES ('The Battle of Marathon', 'The Athenians defeat the Persians in the Battle of Marathon, securing their independence and preserving Greek culture.', '490 BCE', -490, 80);
