INSERT INTO events (name, description, date, relevance) VALUES ('Invention of the Wheel', 'The wheel is invented, revolutionizing transportation and making it easier to move heavy objects.', 'Around 3500 BCE', 95);
INSERT INTO events (name, description, date, relevance) VALUES ('Construction of the Great Pyramid of Giza', 'The Great Pyramid of Giza is built as a tomb for the Pharaoh Khufu, showcasing the architectural and engineering skills of ancient Egyptians.', 'Around 2560 BCE', 90);
INSERT INTO events (name, description, date, relevance) VALUES ('Buddha's Enlightenment', 'Siddhartha Gautama attains enlightenment under the Bodhi tree, becoming the Buddha and founding Buddhism.', 'Around 500 BCE', 85);
INSERT INTO events (name, description, date, relevance) VALUES ('The Roman Empire', 'The Roman Empire is established, becoming one of the most powerful and influential civilizations in history.', '27 BCE', 95);
INSERT INTO events (name, description, date, relevance) VALUES ('The Fall of the Western Roman Empire', 'The Western Roman Empire collapses due to various factors, marking the end of ancient Rome and the beginning of the Middle Ages.', '476 CE', 90);
INSERT INTO events (name, description, date, relevance) VALUES ('The Black Death', 'The Black Death, a devastating pandemic caused by the bubonic plague, spreads across Europe, resulting in the death of millions of people.', '1347-1351 CE', 95);
INSERT INTO events (name, description, date, relevance) VALUES ('The Renaissance', 'The Renaissance, a period of cultural and intellectual rebirth, begins in Italy, leading to significant advancements in art, science, and literature.', '14th-17th centuries CE', 90);
INSERT INTO events (name, description, date, relevance) VALUES ('The American Revolution', 'The American colonies declare independence from Great Britain, leading to the formation of the United States of America.', '1775-1783 CE', 95);
INSERT INTO events (name, description, date, relevance) VALUES ('The Industrial Revolution', 'The Industrial Revolution begins in Britain, transforming society through the mechanization of production and the development of new technologies.', '18th-19th centuries CE', 95);
INSERT INTO events (name, description, date, relevance) VALUES ('World War I', 'World War I, also known as the Great War, breaks out, involving major global powers and resulting in significant loss of life and territorial changes.', '1914-1918 CE', 95);
INSERT INTO events (name, description, date, relevance) VALUES ('The Moon Landing', 'Apollo 11 successfully lands astronauts Neil Armstrong and Buzz Aldrin on the moon, marking a major milestone in space exploration.', '1969 CE', 90);
INSERT INTO events (name, description, date, relevance) VALUES ('The Fall of the Berlin Wall', 'The Berlin Wall, which divided East and West Germany, falls, symbolizing the end of the Cold War and the reunification of Germany.', '1989 CE', 90);