INSERT INTO events (name, description, year, relevance) VALUES ('Invention of the Wheel', 'The wheel is invented, revolutionizing transportation and making it easier to move heavy objects.', 3500, 95);
INSERT INTO events (name, description, year, relevance) VALUES ('Construction of the Great Pyramid of Giza', 'The Great Pyramid of Giza is built as a tomb for the Pharaoh Khufu, showcasing the architectural and engineering skills of ancient Egyptians.', 2560, 90);
INSERT INTO events (name, description, year, relevance) VALUES ('Buddha's Enlightenment', 'Siddhartha Gautama attains enlightenment under the Bodhi tree, becoming the Buddha and founding Buddhism.', 528, 85);
INSERT INTO events (name, description, year, relevance) VALUES ('Fall of the Roman Empire', 'The Western Roman Empire collapses, marking the end of ancient Rome and the beginning of the Middle Ages.', 476, 90);
INSERT INTO events (name, description, year, relevance) VALUES ('Voyage of Christopher Columbus', 'Christopher Columbus sets sail from Spain and discovers the Americas, leading to European colonization and the Columbian Exchange.', 1492, 95);
INSERT INTO events (name, description, year, relevance) VALUES ('American Revolution', 'Thirteen American colonies declare independence from British rule, leading to the formation of the United States of America.', 1776, 90);
INSERT INTO events (name, description, year, relevance) VALUES ('Industrial Revolution', 'The Industrial Revolution begins in Great Britain, transforming society through advancements in manufacturing, transportation, and agriculture.', 1760, 95);
INSERT INTO events (name, description, year, relevance) VALUES ('World War I', 'A global conflict erupts, involving major world powers and resulting in significant loss of life and political changes.', 1914, 95);
INSERT INTO events (name, description, year, relevance) VALUES ('Atomic Bombing of Hiroshima and Nagasaki', 'The United States drops atomic bombs on the Japanese cities of Hiroshima and Nagasaki, leading to Japan's surrender and the end of World War II.', 1945, 95);
INSERT INTO events (name, description, year, relevance) VALUES ('Moon Landing', 'Apollo 11 successfully lands astronauts Neil Armstrong and Buzz Aldrin on the moon, marking a major milestone in space exploration.', 1969, 90);